<?php class ProdutopackHelper extends Helper{    var $helpers = array('Html');        function view_produto($produto, $categorias, $login_group_id){                		//verifica se pode exibir o produto, dependendo do usuário caso esteja desativado                                $categoria_nome = '';                foreach ($categorias as $cat){                    if($cat['Categoria']['id'] == $produto['Subcategoria']['categoria_id'])                        $categoria_nome = $cat['Categoria']['nome'];                }                		$pode_ver = false;		if($produto['Produto']['ativo'] == 0){			if($login_group_id == 1)				$pode_ver = true;		}else{			$pode_ver = true;		}				if($pode_ver ){			?>                        <div class="produto_bg" >                            <div class="produto"                             <?php                             if($login_group_id == 1){                                if($produto['Produto']['ativo'] == 0){                                    echo 'style="background: #EEE;"';                                 }elseif($produto['Produto']['venda_somente_loja'] == 1){                                    //echo 'style="background: #FFF5D8;"';                                  }                             }                            ?>                            >                                <?php                                 //verifica se esta em promoção                                if($produto['Produto']['promocao'] == 1){                                    ?>                                    <div class="promocao">                                        <?php echo intval($produto['Produto']['desconto']) ?>%                                    </div>                                    <?php                                }                                ?>                                                                <?php                                //insere icone caso a venda seja somente na loja                                if($produto['Produto']['ativo'] == 0){                                    echo $this->Html->image('icon/cross.png', array('class' => 'tipHoverBottom', 'title' => 'Destivado', 'class' => 'venda_loja', 'style' => 'width: 16px;height: 16px;'));                                }else{                                    if($produto['Produto']['venda_somente_loja'] == 1){                                        //echo $this->Html->image('venda_loja.png', array('alt' => 'Visualizar', 'class' => 'venda_loja', 'title' => 'Venda somente na loja', 'alt' => 'Venda somente na loja'));                                    }                                }                                ?>                                <div style="height:143px    ;text-align: center;width: 100%;position: relative">                                    <?php                                    //verifica se esta em lançamento                                    if($produto['Produto']['lancamento'] == 1){                                        ?>                                        <div class="lancamento">                                            New                                        </div>                                        <?php                                    }                                    ?>                                    <div class="produto_foto" >                                                                                        <?php if($login_group_id == 1): ?>                                                    <span class="icons" style="text-align:center;width:77px;margin-right: 70px;">                                                            <?php echo $this->Html->image('icon/find.png', array('alt' => 'Visualizar', 'url' => '/produtos/view_admin/'.$produto['Produto']['id'].'/'.$produto['Produto']['nome'], 'class' => 'tipHoverTop', 'title' => 'Visualizar como<br/> administrador'));?>                                                            <?php echo $this->Html->image('icon/page_white_edit.png', array('alt' => 'Editar', 'url' => '/produtos/edit/'.$produto['Produto']['id'], 'class' => 'tipHoverTop', 'title' => 'Editar dados'));?>                                                            <?php                                                             echo $this->Html->link(                                                                $this->Html->image("icon/bin_closed.png", array("alt" => "Remover",'class' => 'tipHoverTop', 'title' => 'Remover produto')),                                                                '/produtos/delete/'.$produto['Produto']['id'],                                                                array('escape' => false),                                                                'Tem certeza que deseja remover este produto?'                                                            );                                                            ?>                                                    </span>                                                    <br>                                            <?php endif; ?>                                            <?php                                             if(empty($produto['Produto']['img_mini']))                                                $url_imagem = $this->Html->image('/img/sem_foto.png', array("alt" => "[Imagem]".$produto['Produto']['nome']));                                            else                                                $url_imagem = $this->Html->image('/fotos/'.$produto['Produto']['img_mini'], array("alt" => "[Imagem]".$produto['Produto']['nome']));                                            echo $this->Html->link(                                                    $url_imagem,                                                    '/produtos/view/'.$produto['Produto']['id'].'/'.$produto['Produto']['nome'],                                                    array('escape' => false)                                            );                                            ?>                                            <div style="clear:both"></div>                                    </div>                                    <div style="clear:both"></div>                                </div>                                <div style="height: 90px;text-align: center;margin-top: 17px;">                                    <p>                                            <?php                                                                                         echo $this->Html->link(                                                    $produto['Produto']['nome'],                                                    '/produtos/view/'.$produto['Produto']['id'].'/'.$produto['Produto']['nome'],                                                    array('style' => 'font-weight:bold')                                            );                                            ?>                                            <br/>                                            <?php                                              echo $this->Html->link(                                                    $produto['Subcategoria']['nome'],                                                    '/produtos/filtro?busca_flag=flag&subcategoria_id='.$produto['Subcategoria']['id'],                                                    array('style' => 'color:black', 'class' => 'tipHoverRight', 'title' => 'Filtrar por Categoria')                                            );                                            ?>                                            <br/>                                            <?php                                             if(!empty($produto['Marca']['nome'])){                                                echo $this->Html->link(                                                        '+ '.$produto['Marca']['nome'],                                                        '/produtos/filtro?busca_flag=flag&marca_id='.$produto['Marca']['id'],                                                        array('style' => 'color:black', 'class' => 'tipHoverRight', 'title' => 'Filtrar por Marca')                                                );                                                echo '<br/>';                                            }                                            ?>                                            <div align="center" class="produto_valor" <?php if($produto['Produto']['ativo'] == 0) echo 'style="color:#777"';  ?> >                                                    <table>                                                            <tr>                                                                    <td>                                                                            <?php if($produto['Produto']['promocao'] == 1): ?>                                                                                    <span style="color: #8F8F8F;">																						<strong>De:</strong> <span style="text-decoration:line-through;"> <?php echo $produto['Moeda']['sigla'].' '.$produto['Produto']['valor_form'] ?></span>																					</span><br/>                                                                                    <strong>Por:                                                                                    <?php                                                                                     //calcular valor com  desconto                                                                                    $valor_desconto = $produto['Produto']['valor']*(100-$produto['Produto']['desconto'])/100;                                                                                    $valor_desconto = number_format($valor_desconto,2);                                                                                    $valor_desconto = str_replace(',', '', $valor_desconto);                                                                                    $valor_desconto = str_replace('.', ',', $valor_desconto) ;                                                                                    echo $produto['Moeda']['sigla'].' '.$valor_desconto;                                                                                     ?>                                                                                    </strong>                                                                            <?php else: ?>                                                                                    <strong>Por: <?php echo $produto['Moeda']['sigla'].' '.$produto['Produto']['valor_form'] ?></strong>                                                                            <?php endif; ?>                                                                    </td>                                                                    <td>                                                                            &nbsp;                                                                    </td>                                                            </tr>                                                    </table>                                            </div>                                    </p>                                </div>                                <div style="clear: both"></div>                            </div>                            <div style="clear:both"></div>                        </div>						<?php		}            }    function view_produto_simples($produto){        $html = '';                $link_produto_img = $this->Html->link(                $this->get_image_mini($produto, 99, 8),                '/produtos/view/'.$produto['Produto']['id'].'/'.$produto['Produto']['nome'],                array('escape' => false, 'target' => '_blank')        );                $link_produto_nome = $this->Html->link(                $produto['Produto']['nome'],                '/produtos/view/'.$produto['Produto']['id'].'/'.$produto['Produto']['nome'],                array('target' => '_blank')        );                $html =             '<div style="border: 2px solid #F0CA52;width: 377px;-moz-border-radius: 8px;-webkit-border-radius: 8px;border-radius: 8px;">                 <table >                <tr>                    <td style="width: 100px;padding: 5px;">                        '.$link_produto_img.'                    </td>                    <td style="padding: 5px;">                        '.$link_produto_nome.'<br/>                        Valor: R$ '.$this->valor_produto($produto).'                    </td>                </tr>                </table>            </div>                ';                return $html;    }        function valor_produto($produto, $formulario = 1){        $valor_produto = 0;        if($produto['Produto']['promocao'] == 1){            //calcular valor com  desconto            $valor_produto = $produto['Produto']['valor']*(100-$produto['Produto']['desconto'])/100;            if($formulario)                $valor_produto = number_format($valor_produto, 2, ',', '');        }else{            if($formulario)                $valor_produto = $produto['Produto']['valor_form'];            else                $valor_produto = $produto['Produto']['valor'];        }                 return $valor_produto;    }        function get_image_mini($produto, $width = null, $radius = 0){                if(!empty($width))            $img_width = 'width:'.$width.'px;';        else            $img_width = '';                if(!empty($radius))            $img_radius = '-moz-border-radius: '.$radius.'px;-webkit-border-radius: '.$radius.'px;border-radius: '.$radius.'px;';        else            $img_radius = '';                $url_imagem = '';        if(empty($produto['Produto']['img_mini']))            $url_imagem = $this->Html->image('/img/sem_foto.png', array("alt" => "[Imagem]".$produto['Produto']['nome'], 'style' => $img_width.$img_radius));        else            $url_imagem = $this->Html->image('/fotos/'.$produto['Produto']['img_mini'], array("alt" => "[Imagem]".$produto['Produto']['nome'], 'style' => $img_width.$img_radius));        return $url_imagem;    }    }?>